#include "../minishell.h"

int	envlen(char *env)
{
	int		i;

	if (!env || (!ft_isalpha(env[0]) && env[0] != '_'))
		return (0);
	i = 1;
	while (ft_isalnum(env[i]) || env[i] == '_')
		i++;
	return (i);
}
